/**
 * @name Analytics
 */
export class Analytics {
    _plugins;
    constructor() {
        this._plugins = [];
    }
    config(plugins) {
        this._plugins = plugins;
    }
    trackEvent(category, action, additionalData) {
        this._plugins.forEach(plugin => {
            plugin.trackEvent(category, action, additionalData);
        });
    }
}
export default new Analytics();
